import{_ as i,a as e,b as l,c as t}from"./chunks/20241222-4.CLwgN2iY.js";import{_ as r,c as o,o as h,ae as p}from"./chunks/framework.tuuL-SzM.js";const f=JSON.parse('{"title":"背部训练-上背和肩后束大小圆肌肉的训练","description":"2024-12-22 今天喝健身房的大哥新学的几个动作，有一些新的感悟","frontmatter":{"title":"背部训练-上背和肩后束大小圆肌肉的训练","description":"2024-12-22 今天喝健身房的大哥新学的几个动作，有一些新的感悟"},"headers":[],"relativePath":"about/fitness/training/warmUp2.md","filePath":"about/fitness/training/warmUp2.md"}'),n={name:"about/fitness/training/warmUp2.md"};function s(d,a,u,c,m,b){return h(),o("div",null,a[0]||(a[0]=[p('<h1 id="背部训练-上背和肩后束-新动作" tabindex="-1">背部训练-上背和肩后束（新动作） <a class="header-anchor" href="#背部训练-上背和肩后束-新动作" aria-label="Permalink to &quot;背部训练-上背和肩后束（新动作）&quot;">​</a></h1><p>圆肩肱骨前移体态问题，之前一直很苦恼练了好久没效果，后来在网上看到一个比较懂解剖的教练讲到，他的肱骨前移用了整整五年才完全改好，再想想自己上背部肩袖的训练其实也就是俩年，以前20多年的不好的习惯形成的体态问题，需要更多更狠的训练也许才能抵消。</p><p>所以近期的健身计划关于背部训练，要改下训练方式。充分热身后，让肩胛骨回到正常位置，正式训练以上背为主，狠练肩后束，加强中下斜方/菱形肌的训练，最后练下背部。</p><p>今天新学到几个动作，感觉肩后束比较有充血感，记录分享下。</p><h4 id="一、颈后高位下拉" tabindex="-1">一、颈后高位下拉 <a class="header-anchor" href="#一、颈后高位下拉" aria-label="Permalink to &quot;一、颈后高位下拉&quot;">​</a></h4><p>颈后高位下拉可作为热身动作，可以使肩袖肌群热身到更加灵活的状态，让可活动的范围变大</p><p>1、颈后高位下拉</p><p>1.1. 动作要领：</p><ul><li>身体微微前倾，弓着点背，卧距中宽（因为我的肩比较窄，不要太宽），往颈后下拉。</li></ul><p>1.2. 目的：</p><ul><li>如果重量比较轻特别适合热身，能使肩后束以及周围肌群活动范围变大些、更加灵活。如果重量重一些能够练到上背的部分。</li></ul><h4 id="二、正向高位下拉-练中下斜方" tabindex="-1">二、正向高位下拉（练中下斜方） <a class="header-anchor" href="#二、正向高位下拉-练中下斜方" aria-label="Permalink to &quot;二、正向高位下拉（练中下斜方）&quot;">​</a></h4><p>以前一直使用窄距的把手下拉练中下斜方，现在使用宽距的杆练中下斜方，发力感受更好。同坐姿划船一样，窄距练整体，宽距练中间，同样适用于下拉，还是宽距的发力感好。</p><p>1、正向高位下拉</p><p>1.1. 动作要领</p><ul><li>握距宽一些，身体稍微后仰，感受中下斜方肌肉的发力</li></ul><p>1.2. 目的</p><ul><li>同坐姿划船一样，窄距练整体，宽距练中间，同样适用于下拉，还是宽距的使中间发力更好。</li></ul><h4 id="三、龙门架附身颈后下拉-这个动作我也不知道叫什么名字" tabindex="-1">三、龙门架附身颈后下拉（这个动作我也不知道叫什么名字） <a class="header-anchor" href="#三、龙门架附身颈后下拉-这个动作我也不知道叫什么名字" aria-label="Permalink to &quot;三、龙门架附身颈后下拉（这个动作我也不知道叫什么名字）&quot;">​</a></h4><p>在龙门架找个稳定脚的垫子，脚踩着稳住下半身，附身使用中宽的把手往颈后拉，或者颈前拉，颈前类似有点绳索面拉的感觉。颈后拉这个动作我感觉肩后束以及周围练完感觉充血感特别好。</p><p>1、龙门架附身颈后下拉</p><p>1.1. 动作要领</p><h4 id="使用这样的把手" tabindex="-1">使用这样的把手 <a class="header-anchor" href="#使用这样的把手" aria-label="Permalink to &quot;使用这样的把手&quot;">​</a></h4><p><img src="'+i+'" alt="使用这样的把手" width="400" height="auto"></p><h4 id="使用一个垫子稳定下半身、固定脚的位置" tabindex="-1">使用一个垫子稳定下半身、固定脚的位置 <a class="header-anchor" href="#使用一个垫子稳定下半身、固定脚的位置" aria-label="Permalink to &quot;使用一个垫子稳定下半身、固定脚的位置&quot;">​</a></h4><p><img src="'+e+'" alt="使用这样的把手" width="400" height="auto"></p><h4 id="附身这样的位置-这个还不标准-慢慢调整吧" tabindex="-1">附身这样的位置（这个还不标准，慢慢调整吧） <a class="header-anchor" href="#附身这样的位置-这个还不标准-慢慢调整吧" aria-label="Permalink to &quot;附身这样的位置（这个还不标准，慢慢调整吧）&quot;">​</a></h4><p><img src="'+l+'" alt="使用这样的把手" width="400" height="auto"></p><h4 id="颈后下拉这样一个轨迹-或者颈前拉-类似绳索面拉的感觉-头不要保持不动-拉回来的时候仰头-升上去的时候头在低下来-以防碰到脸" tabindex="-1">颈后下拉这样一个轨迹，或者颈前拉（类似绳索面拉的感觉，头不要保持不动，拉回来的时候仰头，升上去的时候头在低下来，以防碰到脸） <a class="header-anchor" href="#颈后下拉这样一个轨迹-或者颈前拉-类似绳索面拉的感觉-头不要保持不动-拉回来的时候仰头-升上去的时候头在低下来-以防碰到脸" aria-label="Permalink to &quot;颈后下拉这样一个轨迹，或者颈前拉（类似绳索面拉的感觉，头不要保持不动，拉回来的时候仰头，升上去的时候头在低下来，以防碰到脸）&quot;">​</a></h4><p><img src="'+t+'" alt="使用这样的把手" width="400" height="auto"></p><p>1.1. 目的</p><ul><li>颈后下拉这个动作呢会感觉到肩后束及周围肌群有很明显的充血感，比使用蝴蝶机反向飞鸟感觉更好。颈前我感觉还没练到位，下次再找找感觉。</li><li>肩后束及周围肌群感受更强烈，上背比较酸胀。</li></ul><h4 id="今日训练记录" tabindex="-1">今日训练记录 <a class="header-anchor" href="#今日训练记录" aria-label="Permalink to &quot;今日训练记录&quot;">​</a></h4><h4 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h4><h6 id="热身" tabindex="-1">热身 <a class="header-anchor" href="#热身" aria-label="Permalink to &quot;热身&quot;">​</a></h6><ul><li>热身四件套使肩胛骨回到正常位置（反向高位下拉+头上耸肩 10✖️3，单臂旋转推举+高位下拉送肩胛 10✖️4）</li></ul><h6 id="正式组背-肩后" tabindex="-1">正式组背/肩后 <a class="header-anchor" href="#正式组背-肩后" aria-label="Permalink to &quot;正式组背/肩后&quot;">​</a></h6><ul><li><p>窄距坐姿划船 10✖️4，宽距坐姿划船 10✖️2</p></li><li><p>窄距高位下拉 10✖️4，宽距高位下拉 10✖️3</p></li><li><p>龙门架附身颈后下拉 10✖️3，龙门架附身颈前下拉 10✖️2</p></li><li><p>引体 6✖️2</p></li><li><p>蝴蝶机反向飞鸟 10✖️5</p></li><li><p>山羊挺身 15✖️4</p></li></ul><h6 id="腹部前锯肌" tabindex="-1">腹部前锯肌 <a class="header-anchor" href="#腹部前锯肌" aria-label="Permalink to &quot;腹部前锯肌&quot;">​</a></h6><ul><li>器械 15✖️4</li></ul><h6 id="有氧" tabindex="-1">有氧 <a class="header-anchor" href="#有氧" aria-label="Permalink to &quot;有氧&quot;">​</a></h6><ul><li>爬坡30分钟</li></ul>',42)]))}const P=r(n,[["render",s]]);export{f as __pageData,P as default};
