import{_ as i,c as l,o as e,ae as t}from"./chunks/framework.tuuL-SzM.js";const _=JSON.parse('{"title":"成长型思维的力量","description":"探讨如何通过成长型思维改变生活和职业发展","frontmatter":{"title":"成长型思维的力量","description":"探讨如何通过成长型思维改变生活和职业发展"},"headers":[],"relativePath":"about/life/thoughts/growth-mindset.md","filePath":"about/life/thoughts/growth-mindset.md"}'),o={name:"about/life/thoughts/growth-mindset.md"};function r(h,a,s,n,d,u){return e(),l("div",null,a[0]||(a[0]=[t('<h1 id="成长型思维的力量" tabindex="-1">成长型思维的力量 <a class="header-anchor" href="#成长型思维的力量" aria-label="Permalink to &quot;成长型思维的力量&quot;">​</a></h1><h2 id="什么是成长型思维" tabindex="-1">什么是成长型思维？ <a class="header-anchor" href="#什么是成长型思维" aria-label="Permalink to &quot;什么是成长型思维？&quot;">​</a></h2><p>成长型思维（Growth Mindset）是由斯坦福大学心理学家卡罗尔·德韦克提出的概念。它强调能力和智力并非固定不变，而是可以通过努力、学习和坚持得到持续提升。</p><h2 id="固定型思维-vs-成长型思维" tabindex="-1">固定型思维 vs 成长型思维 <a class="header-anchor" href="#固定型思维-vs-成长型思维" aria-label="Permalink to &quot;固定型思维 vs 成长型思维&quot;">​</a></h2><h3 id="固定型思维" tabindex="-1">固定型思维 <a class="header-anchor" href="#固定型思维" aria-label="Permalink to &quot;固定型思维&quot;">​</a></h3><ul><li>认为能力是天生的</li><li>害怕失败</li><li>逃避挑战</li><li>容易放弃</li></ul><h3 id="成长型思维" tabindex="-1">成长型思维 <a class="header-anchor" href="#成长型思维" aria-label="Permalink to &quot;成长型思维&quot;">​</a></h3><ul><li>相信能力可以通过学习改变</li><li>视失败为学习机会</li><li>乐于接受挑战</li><li>保持好奇和学习的热情</li></ul><h2 id="培养成长型思维的方法" tabindex="-1">培养成长型思维的方法 <a class="header-anchor" href="#培养成长型思维的方法" aria-label="Permalink to &quot;培养成长型思维的方法&quot;">​</a></h2><ol><li>接受并学习于失败</li><li>使用积极的自我对话</li><li>持续学习新技能</li><li>欣赏过程，而非仅关注结果</li><li>向他人学习</li></ol><h2 id="实践建议" tabindex="-1">实践建议 <a class="header-anchor" href="#实践建议" aria-label="Permalink to &quot;实践建议&quot;">​</a></h2><ul><li>每天学习一点新知识</li><li>记录个人成长历程</li><li>设定具体且可实现的目标</li><li>保持开放和谦逊的态度</li></ul>',12)]))}const m=i(o,[["render",r]]);export{_ as __pageData,m as default};
